CREATE TABLE `mtt_shop_goods` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`title` varchar(32) NOT NULL DEFAULT '' COMMENT '名称',`gid` int(11) NOT NULL DEFAULT '0' COMMENT '父级id',`status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态',`create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',`money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '金额', `original_price` decimal(9,2) NOT NULL DEFAULT '0.00' COMMENT '原价', `member_price` decimal(9,2) NOT NULL DEFAULT '0.00' COMMENT '会员价',`reference_cost` varchar(255) NOT NULL DEFAULT '' COMMENT '参考费用',`content` varchar(1000) NOT NULL DEFAULT '' COMMENT '图文详情',`sort` int(11) NOT NULL DEFAULT '0', `introduce` varchar(50) NOT NULL DEFAULT '' COMMENT '简介',`pic` varchar(255) NOT NULL DEFAULT '' COMMENT '图片',`pre_price` decimal(9,2) NOT NULL DEFAULT '0.00' COMMENT '预购价格',`kucun` int(11) NOT NULL DEFAULT '0' COMMENT '库存',`gg_open` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否开启规格',PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='商品表';
CREATE TABLE `mtt_shop_goods_type` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`title` varchar(32) NOT NULL DEFAULT '' COMMENT '分类名称',`sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序',`create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',`status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态',`pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级id',`image` varchar(150) NOT NULL DEFAULT '' COMMENT '分类图片',`type_id` tinyint(1) NOT NULL DEFAULT '0' COMMENT '分类1服务2商品',PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='商品分类表';
CREATE TABLE `mtt_shop_goods_norms` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`gid` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',`title` varchar(128) NOT NULL DEFAULT '' COMMENT '规格名称',`status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态',`create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='商品规格分类';
CREATE TABLE `mtt_shop_goods_norms_type` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`nid` int(11) NOT NULL DEFAULT '0' COMMENT '规格id',`title` varchar(128) NOT NULL DEFAULT '' COMMENT '规格名称',`status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态',`create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',`gid` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='商品规格分类';
CREATE TABLE `mtt_shop_goods_nt` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`ntid` text COMMENT '联合id',`gid` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',`num` int(11) NOT NULL DEFAULT '0' COMMENT '数量',`money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格',`status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态',`create_time` int(11) NOT NULL DEFAULT '0' COMMENT '时间',`member_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '会员价',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `mtt_shop_order` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`order_id` varchar(255) NOT NULL DEFAULT '' COMMENT '订单编号',`name` varchar(255) NOT NULL DEFAULT '' COMMENT '服务名称',`money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '金额',`images` varchar(300) NOT NULL DEFAULT '' COMMENT '图片',`num` int(11) NOT NULL DEFAULT '0' COMMENT '购买数量', `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '下单时间',`status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态',`uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',`address_id` int(11) NOT NULL DEFAULT '0' COMMENT '用户地址id',`is_delete` tinyint(11) NOT NULL DEFAULT '0' COMMENT '删除',`over_time` int(11) NOT NULL DEFAULT '0' COMMENT '结束时间',`g_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品id', `send_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '派遣状态',`send_id` int(11) NOT NULL DEFAULT '0' COMMENT '快递id',`type_id` tinyint(1) NOT NULL DEFAULT '0' COMMENT '类型1服务，2商品',PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE `mtt_express_code` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`order_id` int(11) NOT NULL DEFAULT '0' COMMENT '订单id',`express_id` int(11) NOT NULL DEFAULT '0' COMMENT '物流id',`code` varchar(128) NOT NULL DEFAULT '' COMMENT '快递单号',`status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态',`create_time` int(11) NOT NULL DEFAULT '0' COMMENT '时间',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='快递订单';
