CREATE TABLE `mtt_shop_order`(`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`order_id` int(11) NOT NULL DEFAULT '0' COMMENT '订单编号',`g_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',`money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '金额',`uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户uid',`status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '订单状态',`send_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '派遣状态',`create_time` int(11) NOT NULL DEFAULT '0' COMMENT '派遣时间',PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
